mapper.select:
select CORP_ID, SITE_CD, CHG_NM, CHG_TYPE, CHG_DESC, CHG_SEQ, JOB_GRD, REG_ID, CHG_TEL1, 
CHG_TEL2, CHG_TEL3, CHG_EMAIL, CHG_MOB1, CHG_MOB2, CHG_MOB3, JOB_GRD_NM
from SKYPG.TBLCORP_CHARGE WHERE CORP_ID = #{CORP_ID, jdbcType=VARCHAR}

select CORP_ID, SITE_CD, CHG_NM, CHG_TYPE, CHG_DESC, CHG_SEQ, JOB_GRD, REG_ID, CHG_TEL1, 
CHG_TEL2, CHG_TEL3, CHG_EMAIL, CHG_MOB1, CHG_MOB2, CHG_MOB3, JOB_GRD_NM
from SKYPG.TBLCORP_CHARGE 
WHERE CORP_ID = #{CORP_ID, jdbcType=VARCHAR} AND SITE_CD = #{SITE_CD, jdbcType=VARCHAR} 
AND CHG_TYPE = #{CHG_TYPE, jdbcType=VARCHAR}
